$(document).ready(function(){$(".upload-avatar").change(function(){var e=$(this).closest("form"),t=new FormData(e[0]),a=e.prop("action");$.ajax({url:a,type:"PATCH",beforeSend:function(e){e.setRequestHeader("X-CSRF-Token",$('meta[name="csrf-token"]').prop("content"))},dataType:"JSON",data:t,processData:!1,contentType:!1,success:function(e){$("#avatar").prop("src",e.thumb.url)}})}),$(".filter").click(function(){$(".filter").parent().find(".active").removeClass("active"),$(this).addClass("active")})});